def judge(first, second):  # 判断是否可以进行变换
    if len(first) != len(second):
        return False
    diff = 0
    for value in first:
        if value not in second:
            diff += 1
    return True if diff == 1 else False


def getNext(v, L, history):  # 获得v单词可以进行的所有变换单词
    temp = []
    for i in range(len(L)):
        if history[i] == False and judge(L[i], v):  # 用history进行记录是否已经遍历过，如果已经遍历过就不要再加入队列中，因为前面的单词的步数一定比现在加的要少
            temp.append(L[i])
            history[i] = True
    return temp


def bfs(start, end, L, history):  # 广度优先搜索
    if end == start:
        return 0
    List = []
    List.append((start, 0))  # 用一个tuple表示结点，tuple[0]表示单词，tuple[1]表示这个单词入队时的步数值，父结点的步数值比子结点小1
    history[L.index(start)] = True
    while len(List):
        temp = List.pop(0)
        nextWord = getNext(temp[0], L, history)
        # print nextWord
        for word in nextWord:
            if word == end:
                return temp[1] + 1  # 如果找到了，就返回步数，是父亲结点的加1
            List.append((word, temp[1] + 1))  # 没有的话，就入队列将所有的子结点的步数在父结点的基本上加1即可
            # print List
    return -1


